var resplice = function (array, index) {
	var newArray = []
	for (var i = 0; i < array.length; i++) {
		if (i != index) {
			newArray.push(array[i])

		}
	}
	for (var i = 0; i < newArray.length; i++) {
		newArray[i].serverIndex = i
	}
	return newArray
}


